package JavaBasics;

public class DecrementOperation {

    public static void main(String[] args) {
        // TODO Auto-generated method stub

        int i = 2;

        System.out.println(i); // 10

        System.out.println(i--); // 10

        System.out.println(i); // 9

        System.out.println(--i); // 8

    }
}
