package JavaBasics;

public class WhileLoopDemo {

    public static void main(String[] args) {
        // TODO Auto-generated method stub

        int a = 1;

        System.out.println("Below are 1 to 10 numbers");

        while (a<=10)
        {
            System.out.println(a);

            a = a + 1;

        }

    }
}
