package JavaBasics;

public class WhileLoopDemo2 {

    public static void main(String[] args) {
        // TODO Auto-generated method stub

        int a = 10;

        System.out.println("Below are 10 to 1 numbers");

        while (a>=1)

        {
            System.out.println(a);

            a = a - 1;


        }

    }
}
